export async function addPost(post) {
    try {
      await fetch('http://localhost:3000/posts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(post)
      });
    } catch (err) {
      console.error(err);
    }
  }
