import { createRouter, createWebHistory } from 'vue-router';
import Home from '../views/Home.vue';
import CreatePostView from '../views/CreatePostView.vue';
import PostDetailView from '../views/PostDetailView.vue';

// Définition des routes
const routes = [
  { path: '/', component: Home }, // Route pour la page d'accueil
  { path: '/addPost', component: CreatePostView }, // Route pour la page de création de post
  { path: '/post/:id', component: PostDetailView } // Route pour la page de détail d'un post
];

// Création du routeur avec l'historique du navigateur
const router = createRouter({
  history: createWebHistory(),
  routes
});

export default router;
