import { createRouter, createWebHistory } from 'vue-router';

// Lazy loading des composants
const Home = () => import('../views/Home.vue');
const CreatePostView = () => import('../views/CreatePostView.vue');
const PostDetailView = () => import('../views/PostDetailView.vue');
const NotFound = () => import('../views/NotFound.vue'); // Composant pour les routes inconnues

const routes = [
  {
    path: '/',
    component: Home,
    meta: { title: 'Accueil' } // Meta-information pour le titre de la page
  },
  {
    path: '/addPost',
    component: CreatePostView,
    meta: { title: 'Créer un post' }
  },
  {
    path: '/post/:id',
    component: PostDetailView,
    meta: { title: 'Détails du post' },
    props: true // Passer les paramètres de route en tant que props
  },
  {
    path: '/:pathMatch(.*)*', // Capture toutes les routes inconnues
    component: NotFound,
    meta: { title: 'Page non trouvée' }
  }
];

const router = createRouter({
  history: createWebHistory(),
  routes
});

// Mettre à jour le titre de la page en fonction de la meta-information
router.beforeEach((to, from, next) => {
  const title = to.meta.title || 'Mon Application Vue.js';
  document.title = title;
  next();
});

export default router;
